CREATE OR REPLACE TRIGGER TBU_MAP_PRODUTO_AUD
BEFORE UPDATE ON MAP_PRODUTO
FOR EACH ROW
DECLARE
 vnLoteAuditoria NUMBER;
 vsPDAudProduto varchar2(1);
BEGIN
  sp_buscaparamdinamico('PRODUTO',0,'UTIL_AUD_PRODUTO','S','N', 'UTILIZA CONTROLE DE ALTERAÇÕES NO CADASTRO DE PRODUTO ?',vsPDAudProduto);
  IF vsPDAudProduto = 'S' THEN
    SELECT S_LOTEAUDITORIA.NEXTVAL
      INTO vnLoteAuditoria
      FROM DUAL;
    IF :OLD.INDEMITECODPRODFISCALNFE !=  :NEW.INDEMITECODPRODFISCALNFE  OR (:OLD.INDEMITECODPRODFISCALNFE IS NULL AND  :NEW.INDEMITECODPRODFISCALNFE IS NOT NULL ) OR (:OLD.INDEMITECODPRODFISCALNFE IS NOT NULL AND  :NEW.INDEMITECODPRODFISCALNFE IS NULL )   THEN
      INSERT INTO MAP_AUDITORIA
      (SEQAUDITORIA, DTAHORAUDITORIA, DTAAUDITORIA, USUAUDITORIA, SEQIDENTIFICA, TABELA, ORIGEM , CAMPO,DESCCAMPO, VLRANTERIOR, VLRATUAL,LOTEAUDITORIA,IDENTIFICADOR)
      VALUES (S_SEQAUDITORIA.NEXTVAL, SYSDATE, TRUNC(SYSDATE),:NEW.USUARIOALTERACAO, :NEW.SEQPRODUTO, 'MAP_PRODUTO','PRODUTO' , 'INDEMITECODPRODFISCALNFE','Emite Cod. Prod. Fiscal na NFe', :OLD.INDEMITECODPRODFISCALNFE, :NEW.INDEMITECODPRODFISCALNFE,vnLoteAuditoria,'Produto');
    END IF;
    --
    IF :OLD.PERCDIASADVERRECBTO !=  :NEW.PERCDIASADVERRECBTO  OR (:OLD.PERCDIASADVERRECBTO IS NULL AND  :NEW.PERCDIASADVERRECBTO IS NOT NULL ) OR (:OLD.PERCDIASADVERRECBTO IS NOT NULL AND  :NEW.PERCDIASADVERRECBTO IS NULL )   THEN
